jQuery.ajax({
    url: '//almenassa.blogspot.com/feeds/posts/default?alt=json-in-script&max-results=10&callback=?',
    type: 'get',
	cache: true,
    dataType: "jsonp",
    success: function(data) {
        var posturl = "";
        var htmlcode = '<ul>';
        for (var i = 0; i < data.feed.entry.length; i++) {
                for (var j=0; j < data.feed.entry[i].link.length; j++)
                {
                     if (data.feed.entry[i].link[j].rel == "alternate")
                        {
                            posturl = data.feed.entry[i].link[j].href;
                            break;
                         }
                 }
            if("content" in data.feed.entry[i]){
                var postcontent = data.feed.entry[i].content.$t;
            }else if("summary" in data.feed.entry[i]){
                var postcontent = data.feed.entry[i].summary.$t;
            }else{
                var postcontent = "";
            }

            var re = /<S[^>]*>/g;
            postcontent = postcontent.replace(re,"");
            if(postcontent.length > 130){
                postcontent = postcontent.substring(0,130);
            }
            var posttitle = data.feed.entry[i].title.$t;
            var img = data.feed.entry[i].media$thumbnail.url.replace(/\/s72\-c/, "/s80-c");

            htmlcode += '<a href="'+posturl+'" target="_blank"><div class="latestposts"><img style="float:left;padding: 3px; border: 1px solid #ddd;margin:0 5px" src="' + img + '"/><span class="posttitle">'+posttitle+'</span></div></a>';
        }
        htmlcode += '</ul>';
        document.getElementById('result').innerHTML = htmlcode;
    }
});

});
